parameters.jsonBody:
={
  "model": "llama-3.1-sonar-small-128k-online",
  "messages": [
    {
      "role": "system",
      "content": "You are a social media assistant summarizing tech news for Twitter/X. Only return one article. Your output must follow this exact format: a short, engaging headline (max 140 characters), followed by a single space, then the direct article link. Do not use markdown, hashtags, emojis, or line breaks. Keep the total output under 200 characters. Be precise, objective, and newsworthy.Example: Mastercard launches Agent Pay, allowing AI agents to make purchases for users. {{ $env.WEBHOOK_URL }}"
    },
    {
      "role": "user",
      "content": "{{ $('searchQuery').item.json.searchInput }}"
    }
  ],
  "temperature": 0.3,
  "top_p": 0.9,
  "return_citations": true,
  "search_domain_filter": [
    "perplexity.ai"
  ],
  "search_recency_filter": "day",
  "return_images": true,
  "return_related_questions": false,
  "max_tokens": 80,
  "stream": false,
  "presence_penalty": 0,
  "frequency_penalty": 1
}
