const text = $json["ParsedResults"][0]["ParsedText"];

// Split text by line breaks and trim spaces
const lines = text.split("\n").map(line => line.trim());

// Debugging: Log extracted lines for verification
console.log("Extracted Lines:", lines);

// Helper function to find text after a keyword, with OCR variations
function getValueAfterKeyword(keywords, offset = 1) {
    let index = lines.findIndex(line => keywords.some(keyword => line.includes(keyword)));
    return index !== -1 && lines[index + offset] ? lines[index + offset] : null;
}

// **Extracting Data for Both Standard & PromptPay Transactions**
const transaction_type = lines[0] || null;  // First line
const date_time = lines[1] || null;  // Second line

// **Sender Details**
const sender_name_index = lines.findIndex(line => line.startsWith("นาย"));
const sender_name = sender_name_index !== -1 ? lines[sender_name_index] : null;
const sender_bank = sender_name_index !== -1 ? lines[sender_name_index + 1] : null;
const sender_account = sender_name_index !== -1 ? lines[sender_name_index + 2] : null;

// **Determine if it's a Standard Bank Transfer or PromptPay**
const isPromptPay = lines.some(line => line.includes("Prompt") || line.includes("รหัสพร้อมเพย์"));
let receiver_name = null;
let receiver_bank = null;
let receiver_account = null;

if (isPromptPay) {
    // **Handling PromptPay Transactions**
    const receiver_index = lines.findIndex(line => line.includes("Prompt"));
    receiver_bank = "PromptPay"; // Fixed for PromptPay transactions
    receiver_name = receiver_index !== -1 ? lines[receiver_index + 2] : null; // Receiver's actual name

    // **Fix Receiver Account for PromptPay**
    const receiver_account_index = lines.findIndex(line => line.includes("รหัสพร้อมเพย์"));
    receiver_account = receiver_account_index !== -1 ? lines[receiver_account_index + 1] : null; // The actual account number

} else {
    // **Handling Standard Bank Transfers**
    const receiver_index = lines.findIndex(line => line.includes("นิติบุคคล") || line.includes("บริษัท") || line.includes("นาย"));
    receiver_name = receiver_index !== -1 ? lines[receiver_index] : null;
    receiver_bank = receiver_index !== -1 ? lines[receiver_index + 2] : null;
    receiver_account = receiver_index !== -1 ? lines[receiver_index + 3] : null;
}

// **Fix Transaction ID Extraction**
let transaction_id = null;

// **First, try "เลขที่รายการ:" for Standard Transactions**
const transaction_index = lines.findIndex(line => line.includes("เลขที่รายการ:"));
if (transaction_index !== -1) {
    if (/\d{10,}/.test(lines[transaction_index])) {
        // If the same line contains the transaction ID, extract it
        transaction_id = lines[transaction_index].match(/\d{10,}/)[0];
    } else if (transaction_index + 1 < lines.length && /\d{10,}/.test(lines[transaction_index + 1])) {
        // If transaction ID is on the next line, extract it
        transaction_id = lines[transaction_index + 1];
    }
}

// ✅ **If transaction_id is still missing, use "จำนวน:" or possible OCR errors ("จำนวนะ")**
if (!transaction_id) {
    let amount_index = lines.findIndex(line => line.includes("จำนวน") || line.includes("จำนวนะ"));
    if (amount_index !== -1) {
        for (let i = amount_index + 1; i < lines.length; i++) {
            if (/^[A-Za-z0-9]+$/.test(lines[i])) { // Ensure it's a valid ID
                transaction_id = lines[i];
                break; // **Break early for efficiency**
            }
        }
    }
}

// **Extract Amount Correctly**
const amount_index = lines.findIndex(line => line.includes("บาท") && !line.includes("ค่าธรรมเนียม"));
const amount = amount_index !== -1 ? lines[amount_index].replace(" บาท", "").replace(/[^0-9.]/g, "") : null;

// **Extract Fee Correctly**
const fee_index = lines.findIndex(line => line.includes("ค่าธรรมเนียม"));
const fee = fee_index !== -1 && lines[fee_index + 1] ? lines[fee_index + 1].replace(" บาท", "").replace(/[^0-9.]/g, "") : null;

// **Ensure Essential Details Exist**
if (transaction_type && date_time && sender_name && sender_bank && sender_account && receiver_name && receiver_bank && receiver_account && transaction_id && amount) {
    return [
        {
            json: {
                "transaction_type": transaction_type,
                "date_time": date_time,
                "sender_name": sender_name,
                "sender_bank": sender_bank,
                "sender_account": sender_account,
                "receiver_name": receiver_name,
                "receiver_bank": receiver_bank,
                "receiver_account": receiver_account,
                "transaction_id": transaction_id,
                "amount": amount,
                "fee": fee
            }
        }
    ];
} else {
    return [
        {
            json: {
                "error": "Some values could not be extracted",
                "raw_text": text
            }
        }
    ];
}
